import React, { useState, useRef, useEffect } from 'react';
import {
  Animated,
  Button,
  Image,
  ImageBackground,
  Platform,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  TouchableWithoutFeedback,
  KeyboardAvoidingView,
  Modal,
  View,
  Dimensions,
} from 'react-native';

const { width, height } = Dimensions.get("screen")

import s from '../../../styles/style'
import text from '../../../assets/text/text'

import TopGradient from '../../../components/TopGradient'
import AvaGradient from '../../../components/AvaGradient'

import BlogModal from './BlogModal'


let platform = Platform.OS === 'ios' ? true : false

import SvgUri from "expo-svg-uri"
import RnModal from 'react-native-modal';
import Carousel, { Pagination } from 'react-native-snap-carousel';
import { Video, AVPlaybackStatus } from 'expo-av';

export default function Screen(props) {

  let lang = props.lang
  let users = props.users
  let navigation = props.navigation



  let [search, setSearch] = React.useState(false)
  let [blogModal, setBlogModal] = React.useState(false)
  let [post, setPost] = React.useState(null)
  let [item, setItem] = React.useState(0)


  let group = { nick: '@hypefans', ava: require('../../../assets/images/ava5.png'), name: 'HypeFans' }
  let posts = [
    {
      group: group, author: users[0], time: '20 Ð¼Ð¸Ð½ Ð½Ð°Ð·Ð°Ð´', text: 'ÐŸÐµÑ€Ñ‡Ð°Ñ‚ÐºÐ¸ ÑÐ½ÑÑ‚Ñ‹, Ð¿Ð¾ÑÐºÐ¾Ð»ÑŒÐºÑƒ Ñ‡ÐµÐ¼Ð¿Ð¸Ð¾Ð½ Ð¿Ð¾ Ð±Ð¾ÐºÑÑƒ @TonyBellew Ð¿Ñ€Ð¸ÑÐ¾ÐµÐ´Ð¸Ð½Ð¸Ð»ÑÑ Ðº Ð½Ð°Ð¼ Ð½Ð° HypeFans ðŸ¥Š ÐžÐ½ Ð¿Ñ€ÐµÐ´Ð»Ð°Ð³Ð°ÐµÑ‚ Ð²Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ð¾ÑÑ‚ÑŒ Ð¿Ñ€Ð¸ÑÐ¾ÐµÐ´Ð¸Ð½Ð¸Ñ‚ÑŒÑÑ, Ð¿Ð¾ÑÐºÐ¾Ð»ÑŒÐºÑƒ Ñ‡ÐµÐ¼Ð¿Ð¸Ð¾Ð½ Ð¿Ð¾ Ð±Ð¾ÐºÑÑƒ @TonyBellew Ð¿Ñ€Ð¸ÑÐ¾ÐµÐ´Ð¸Ð½Ð¸Ð»ÑÑ Ðº Ð½Ð°Ð¼ Ð½Ð° HypeFans ðŸ¥Š ÐžÐ½ Ð¿Ñ€ÐµÐ´Ð»Ð°Ð³Ð°ÐµÑ‚ Ð²Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ð¾ÑÑ‚ÑŒ Ð¿Ñ€Ð¸ÑÐ¾ÐµÐ´Ð¸Ð½Ð¸Ñ‚ÑŒÑÑ.',
      liks: 88, comments: 21, video: require('../../../assets/images/preview1.png'),
    },
    {
      group: group, author: users[1], time: '5 Ñ‡Ð°ÑÐ¾Ð² Ð½Ð°Ð·Ð°Ð´', text: 'ÐŸÑ€Ð¸ÑÐ¾ÐµÐ´Ð¸Ð½ÑÐ¹Ñ‚ÐµÑÑŒ Ðº @desiree, ÐºÐ¾Ð³Ð´Ð° Ð¾Ð½Ð° Ð³Ð¾Ñ‚Ð¾Ð²Ð¸Ñ‚ Ð¾Ð´Ð¸Ð½ Ð¸Ð· ÑÐ²Ð¾Ð¸Ñ… Ð»ÑŽÐ±Ð¸Ð¼Ñ‹Ñ… Ñ€ÐµÑ†ÐµÐ¿Ñ‚Ð¾Ð² - Ð²ÐºÑƒÑÐ½Ð¾Ðµ ÐºÑƒÑ€Ð¸Ð½Ð¾Ðµ Ð°Ð´Ð¾Ð±Ð¾! ÐŸÐ¾Ð´ÐºÐ»ÑŽÑ‡Ð°Ð¹Ñ‚ÐµÑÑŒ Ðº Ð½Ð¾Ð²Ñ‹Ð¼ ÑÐµÑ€Ð¸ÑÐ¼ ÐºÐ¾Ð³Ð´Ð° Ð¾Ð½Ð° Ð³Ð¾Ñ‚Ð¾Ð²Ð¸Ñ‚ Ð¾Ð´Ð¸Ð½ Ð¸Ð· ÑÐ²Ð¾Ð¸Ñ… Ð»ÑŽÐ±Ð¸Ð¼Ñ‹Ñ… Ñ€ÐµÑ†ÐµÐ¿Ñ‚Ð¾Ð² - Ð²ÐºÑƒÑÐ½Ð¾Ðµ ÐºÑƒÑ€Ð¸Ð½Ð¾Ðµ Ð°Ð´Ð¾Ð±Ð¾!',
      liks: 160, comments: 56, video: require('../../../assets/images/preview2.png'),
    },
    {
      group: group, author: users[2], time: '12 Ñ‡Ð°ÑÐ¾Ð² Ð½Ð°Ð·Ð°Ð´', text: 'Prepare for takeoff as @rebecca_aviatrix is flying you to higher altitudes on HypeFans âœˆï¸ The pilot is taking you on her aviation journey where you can. Prepare for takeoff as @rebecca_aviatrix is flying you to higher altitudes on HypeFans âœˆï¸ The pilot is taking you on her aviation journey where you can.',
      liks: 74, comments: 36, video: require('../../../assets/images/preview3.png'),
    },
    {
      group: group, author: users[6], time: 'Ð’Ñ‡ÐµÑ€Ð°', text: 'Flip into action with pro skateboarder @officialdanimal ðŸ¤˜ Heâ€™s here to wow you with his craziest skills and teach you how to freestyle it',
      liks: 154, comments: 98, video: require('../../../assets/images/preview4.png'),
    },
    {
      group: group, author: users[7], time: 'ÐœÐ°Ñ€Ñ‚, 21', text: 'Yaâ€™ll ainâ€™t ready for this! Itâ€™s @rampage_jackson ðŸ‘ŠðŸ’¥ Itâ€™s going to be a knockout as the former champ is inviting you to the Rampage show',
      liks: 140, comments: 70, video: require('../../../assets/images/preview5.png'),
    },
  ]

  // Carousel data
  let cData = []
  let arr = []
  users.map((user, index) => {

    arr.push(user)
    if (index % 3 == 2) {
      cData.push(arr)
      arr = []
    }
    if (index == users.length - 1) {
      cData.push(arr)
    }
  })



  let _renderItem = ({ item, index0 }) => {
    return (
      item.map((user, index1) => {
        return (
          <TouchableOpacity key={index1} style={[s.photoView, s.center, s.mb5]}
            activeOpacity={0.9}
            onPress={() => navigation.navigate('UserPage', { user: user })}
          >

            <Image style={[s.photoViewAlso, s.pabsolute]}
              source={user.photo} />

            <View style={[s.flexRow]}>
              <View style={[s.autorView, s.flexRow, s.aCenter, s.ml30]}>
                <Image
                  style={[s.image50r, s.mr10]}
                  source={user.ava}
                />
                <View style={[]}>
                  <Text numberOfLines={1} style={[s.text14, s.factorBold, s.textBlack, s.mt3]}>{user.name}</Text>
                  <Text numberOfLines={1} style={[s.text14, s.factor, s.textGrey2, s.mt3]}>{user.nick}</Text>
                </View>
              </View>
              <View style={[s.flex1]} />
            </View>

            {
              index1 % 2 == 0
                ?
                <View style={[s.freeView, s.center, s.mh15, s.pabsolute, { top: 10, right: 15 }]}>
                  <Text style={[s.text12, s.factorBold]}>{text[lang].free}</Text>
                </View>
                : null
            }
          </TouchableOpacity>
        )
      })
    )

  }

  {/* --- Carousel ---  */ }
  let _also = () => {
    return (
      < View style={[s.carouselView, s.mt25]} >
        <Text style={[s.text18, s.factorBold, s.textBlack, s.mh15, s.mt15, s.mb15]}>{text[lang].also}</Text>
        <Carousel
          data={cData}
          //data={carouselData}
          renderItem={_renderItem}
          sliderWidth={width}
          itemWidth={width}
          onSnapToItem={(index) => { setItem(index) }}
          autoplay={false}
          //autoplayDelay={1000}
          //autoplayInterval={3000}
          loop={true}
        />
        <Pagination
          dotsLength={cData.length}
          activeDotIndex={item}
          dotStyle={s.carouselDot}
          inactiveDotOpacity={0.2}
          inactiveDotScale={0.7}
        />
      </View >

    )
  }

  let _stopPlayer = () => {
    //console.log(AVPlaybackStatus)
  }
  const scrollOffsetY = useRef(new Animated.Value(0)).current;



  return (
    <View style={[s.container, s.backColor]}>

      {search
        ?
        <View style={[s.topBar, s.flexRow, s.spaceBtw, s.aCenter, s.statBarMargin, s.greyBottom]}>
          <TouchableOpacity style={[s.btn50, s.center]}
            activeOpacity={0.8}
            onPress={() => setSearch(false)}
          >
            <SvgUri width="24" height="24"
              source={require('../../../assets/images/x.svg')} />
          </TouchableOpacity>

          <TextInput
            // autoFocus={platform ? true : false}
            autoFocus={true}
            style={[s.text14, s.factor, s.textBlack, s.flex1, s.h46, platform ? s.mt3 : null]}
            placeholder={text[lang].search}
            placeholderTextColor={'#aaa'}
            // onFocus={() => platform ? null : props.setTab(false)}
            // onBlur={() => platform ? null : props.setTab(true)}
            onEndEditing={() => { setSearch(false) }}
          />

          <TouchableOpacity style={[s.btn50, s.center]}
            activeOpacity={0.8}
            onPress={() => setSearch(!search)}
          >
            <SvgUri width="24" height="24"
              source={require('../../../assets/images/search.svg')} />
          </TouchableOpacity>
        </View>
        :
        <View style={[s.topBar, s.flexRow, s.spaceBtw, s.aCenter, s.statBarMargin]}>
          <TouchableOpacity style={[s.h50, s.flexRow, s.center, s.ml15]}
            activeOpacity={0.8}
          //onPress={() => backAction()}
          >
            <Image
              style={[s.image32, s.mr10]}
              source={require('../../../assets/images/logo_transp.png')}
            />
            <Text style={[s.text18, s.factorBold, s.textBlack]}>{'HypeFans'}</Text>
          </TouchableOpacity>

          <TouchableOpacity style={[s.btn50, s.center]}
            activeOpacity={0.8}
            onPress={() => setSearch(!search)}
          >
            <SvgUri width="24" height="24"
              source={require('../../../assets/images/search.svg')} />
          </TouchableOpacity>
        </View>
      }

      <TopGradient />



      {/* <KeyboardAvoidingView style={[]}
        behavior={platform ? "padding" : "height"}
      > */}
      <ScrollView style={[]} showsVerticalScrollIndicator={false}
        onScroll={() => _stopPlayer()}
        // onScroll={
        //   Animated.event([
        //     { nativeEvent: { contentOffset: { y: scrollOffsetY } } }
        //   ],
        //     { useNativeDriver: false })
        // }
        scrollEventThrottle={16}
      >

        {/* --- Stories ---  */}

        <ScrollView style={[s.flexRow, s.mt15]} horizontal={true} showsHorizontalScrollIndicator={false}>
          <TouchableOpacity style={[s.ava, s.center, s.mr5, s.ml10]}
            activeOpacity={0.8}
            onPress={() => navigation.navigate('Story')}
          >
            <View style={[s.userIcon, s.center]}>
              <Image
                style={[s.image60rw]}
                source={require('../../../assets/images/ava1.png')}
              />
            </View>
            <View style={[s.addBtn, s.pabsolute]}>
              <SvgUri width="20" height="20"
                source={require('../../../assets/images/add.svg')} />
            </View>
            <Text numberOfLines={1} style={[s.text14, s.factor, s.textGrey2, s.mt3]}>{text[lang].yrStory}</Text>
          </TouchableOpacity>

          {
            users.map((user, index) => {
              return (
                <TouchableOpacity key={index}
                  style={[s.ava, s.center, s.mr5]}
                  activeOpacity={0.8}
                  onPress={() => navigation.navigate('StoryViewer')}
                >
                  <View style={[s.personIcon, s.center]}>
                    <AvaGradient style={[s.pabsolute]} />
                    <View style={[s.whiteRound1, s.pabsolute]} />

                    <Image
                      style={[s.image60rw]}
                      source={user.ava}
                    />
                  </View>
                  <Text numberOfLines={1} style={[s.text14, s.factor, s.textBlack, s.mt3]}>{user.nick}</Text>
                </TouchableOpacity>

              )
            })
          }
        </ScrollView>

        {/* --- What you think ---  */}
        <View style={[s.flexRow, s.aCenter, s.mh15, s.mt25]}>

          <View style={[s.center, s.mr10]}>
            <Image
              style={[s.image50r]}
              source={require('../../../assets/images/ava1.png')}
            />
          </View>
          <TextInput
            style={[s.blogInput, s.text16, s.factor, s.textBlack, s.flex1, s.ph15]}
            placeholder={text[lang].urThought}
            placeholderTextColor={'#000'}
          // onFocus={() => platform ? null : props.setTab(false)}
          // onBlur={() => platform ? null : props.setTab(true)}
          //onEndEditing={() => { console.log('post') }}
          />
        </View>


        {/* --- Posts ---  */}
        {
          posts.map((post, index) => {

            let [full, setFull] = React.useState(false)
            let [like, setLike] = React.useState(false)
            let [likeCount, setLikeCount] = React.useState(post.liks)
            let [lines, setLines] = React.useState(null)

            const [show, setShow] = React.useState(false);
            const [status, setStatus] = React.useState({});
            const video = React.useRef(null);


            return (
              <View key={index} style={[s.post, s.mt25]}>

                <View style={[s.groupView, s.flexRow, s.spaceBtw, s.aCenter, s.ml15]}>
                  <View style={[s.flexRow, s.aCenter]}>
                    <Image
                      style={[s.image50r, s.mr10]}
                      source={group.ava}
                    />
                    <View style={[]}>
                      <Text style={[s.text14, s.factorBold, s.textBlack, s.mt3]}>{group.name}</Text>
                      <Text style={[s.text14, s.factor, s.textGrey2, s.mt3]}>{group.nick}</Text>
                    </View>
                  </View>

                  <View style={[s.flexRow, s.aCenter]}>
                    <Text style={[s.text14, s.factor, s.textGrey2]}>{post.time}</Text>
                    <TouchableOpacity style={[s.btn40, s.center]}
                      activeOpacity={0.8}
                      onPress={(post) => {
                        setBlogModal(true)
                        setPost(post)
                      }}
                    >
                      <SvgUri width="5" height="15"
                        source={require('../../../assets/images/3dots.svg')} />
                    </TouchableOpacity>
                  </View>
                </View>

                <TouchableOpacity style={[s.textView, s.mh15, s.mt15]}
                  activeOpacity={1}
                  onPress={() => setFull(!full)}
                >
                  <Text numberOfLines={full ? null : 3} style={[s.text14, s.textLine21, s.factor, s.textBlack]}
                    onTextLayout={({ nativeEvent: { lines } }) => {
                      setLines(lines.length)
                    }}
                  >{post.text}</Text>
                  {
                    full
                      ?
                      null
                      : <View style={[]}>
                        {
                          lines > 3
                            ? <Text style={[s.h15, s.text14, s.factor, s.textOrange]}>{text[lang].redmor}</Text>
                            : null
                        }
                      </View>
                  }
                </TouchableOpacity>

                <TouchableOpacity style={[s.userBar]}
                  activeOpacity={0.9}
                  onPress={() => { navigation.navigate('UserPage', { user: post.author }) }}
                >
                  <ImageBackground style={[s.photoView, s.jCenter, s.mt15]}
                    source={post.author.photo} >

                    <View style={[s.flexRow]}>
                      <View style={[s.autorView, s.flexRow, s.aCenter, s.mh15]}>
                        <Image
                          style={[s.image50r, s.mr10]}
                          source={post.author.ava}
                        />
                        <View style={[]}>
                          <Text numberOfLines={1} style={[s.text14, s.factorBold, s.textBlack]}>{post.author.name}</Text>
                          <Text numberOfLines={1} style={[s.text14, s.factor, s.textGrey2, s.mt3]}>{post.author.nick}</Text>
                        </View>
                      </View>
                      <View style={[s.flex1]} />
                    </View>

                    <View style={[s.freeView, s.center, s.mh15, s.pabsolute, { top: 5, right: 0 }]}>
                      <Text style={[s.text12, s.factorBold]}>{text[lang].free}</Text>
                    </View>
                  </ImageBackground>
                </TouchableOpacity>

                {
                  show
                    ?
                    <TouchableOpacity style={[s.videoView, s.jCenter, s.mt8]}
                      activeOpacity={1}
                      onPress={() => status.isPlaying ? video.current.pauseAsync() : video.current.playAsync()}
                    >
                      <Video
                        ref={video}
                        style={s.videoView}
                        source={{
                          uri: 'http://d23dyxeqlo5psv.cloudfront.net/big_buck_bunny.mp4',
                        }}
                        useNativeControls
                        shouldPlay={true}
                        resizeMode="contain"
                        isLooping={false}
                        onPlaybackStatusUpdate={status => setStatus(() => status)}
                      />
                    </TouchableOpacity>
                    :
                    <ImageBackground style={[s.videoView, s.jCenter, s.mt8]}
                      source={post.video} >
                      <TouchableOpacity style={[s.videoView, s.center, s.mt8]}
                        activeOpacity={1}
                      //onPress={() => setShow(true)}
                      >
                        <SvgUri width="50" height="50"
                          source={require('../../../assets/images/play.svg')} />
                      </TouchableOpacity>
                    </ImageBackground>


                }


                <View style={[s.bottomBtns, s.flexRow, s.spaceBtw, s.aCenter, s.mh10]}>
                  <View style={[s.flexRow, s.aCenter]}>
                    <TouchableOpacity style={[s.h40, s.center, s.ph5]}
                      activeOpacity={0.8}
                      onPress={() => {
                        setLike(!like)
                        like ? setLikeCount(likeCount - 1) : setLikeCount(likeCount + 1)
                      }}
                    >
                      {
                        like
                          ?
                          <SvgUri width="21" height="18"
                            source={require('../../../assets/images/liked.svg')} />
                          :
                          <SvgUri width="21" height="18"
                            source={require('../../../assets/images/heart.svg')} />
                      }

                    </TouchableOpacity>
                    <TouchableOpacity style={[s.h40, s.center, s.ph5]}
                      activeOpacity={0.8}
                      onPress={() => console.log('comment')}
                    >
                      <SvgUri width="18" height="18"
                        source={require('../../../assets/images/comment.svg')} />
                    </TouchableOpacity>
                    <TouchableOpacity style={[s.h40, s.center]}
                      activeOpacity={0.8}
                    //onPress={() => setSearch(!search)}
                    >
                      <Text style={[s.text14, s.factor, s.textGrey2, s.ph5]}>{text[lang].donut}</Text>
                    </TouchableOpacity>
                  </View>
                  <TouchableOpacity style={[s.h40, s.center, s.ph5]}
                    activeOpacity={0.8}
                  //onPress={() => setSearch(!search)}
                  >
                    <SvgUri width="18" height="18"
                      source={require('../../../assets/images/bookmark.svg')} />
                  </TouchableOpacity>
                </View>

                <Text style={[s.text14, s.factor, s.textBlack, s.mh15]}>{likeCount + text[lang].liks1}</Text>

                <TouchableOpacity style={[s.h25, s.jCenter, s.mb25]}
                  activeOpacity={0.8}
                //onPress={() => setSearch(!search)}
                >
                  <Text style={[s.text14, s.factor, s.textGrey2, s.mh15,]}>
                    {text[lang].watch + post.comments + text[lang].comments1}
                  </Text>
                </TouchableOpacity>

                {/* --- Carousel ---  */}
                { index == 2 || (posts.length == 1)
                  ? _also()
                  : null
                }

              </View>
            )
          })
        }

        <TouchableOpacity style={[s.h40, s.center, s.mb25]}
          activeOpacity={0.8}
        //onPress={() => setSearch(!search)}
        >
          <Text style={[s.text14, s.factor, s.textOrange, s.mh15]}>
            {text[lang].morePosts}
          </Text>
        </TouchableOpacity>



        <View style={{ height: 60 }} />

      </ScrollView>
      {/* </KeyboardAvoidingView > */}

      <RnModal
        style={{ margin: 0 }}
        coverScreen={false}
        isVisible={blogModal}
        onBackButtonPress={() => setBlogModal(false)}
        // animationIn={'slideInUp'}
        // animationOut={'slideOutDown'}
        animationIn={'fadeIn'}
        animationOut={'fadeOut'}
        scrollOffset={100}
        swipeDirection={'down'}
        onSwipeComplete={() => setBlogModal(false)}
      >
        <BlogModal
          lang={lang}
          onBack={() => setBlogModal(false)}
          post={post}
        />
      </RnModal>


    </View >
  );
}
